package conditional;

public abstract class Ticket {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int ticket=900;
		int money=800;
		
		if(money>=ticket)
		{
			System.out.println("i can travel pune to mumbai ");
		}
		else if(money<=ticket&&money<=ticket)
			{
			System.out.println("i can not travel pune to nagpur");
		}
		else if(money<=500&& money>=400)
		{
			System.out.println("");
		}
		else
		{
			System.out.println("");
		}

	}

}
