package login;

public class Switch_Case {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		String days="friday";
		switch(days)
		{
		case "monday":
			System.out.println("meeting with manager");
			break;
		case "tuesday":
			System.out.println("team work");
			break;
		case "wen":
			System.out.println("employee meeting");
			break;
		case "thursday":
			System.out.println("project work with team member");
			break;
		case "friday":
			System.out.println("company program");
			break;
		case "saturday":
			System.out.println("working with employee");
			break;
		case "sunday":
			System.out.println("meeting session");
			break;
			default:
				System.out.println("holiday");
			}
           }}
