package looping_starement;

public class Star_print 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=5;i++)
		{
			for(j=1;j<=i;j++)
			{
				System.out.print("*");
				}
			System.out.println(" ");
		}
	
   for(i=4;i>=1;i--)
	{
		for(j=i;j>=1;j--)
		
		{
		
		System.out.print("*");
		
	}
		System.out.println(" ");
	}
	}
}
	



