package p1;

public class Assignment_oper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   System.out.println("Assignment operator");
		int a=10,b=5;
		System.out.println(a+=b);
		System.out.println(a-=b);
		System.out.println(a/=b);
		System.out.println(a*=b);
		System.out.println(a%=b);
	}

}
